// src/App.jsx
import React, { useState } from 'react';
import LiveGovernanceStream from './components/LiveGovernanceStream';
import { useLiveGovernanceStream } from './components/LiveGovernanceStream';

function App() {
  const { isStreamOpen, setIsStreamOpen, openStream } = useLiveGovernanceStream();
  const [currentUser] = useState({ type: 'enterprise', name: 'Demo User' });

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-3">
              <div className="h-8 w-8 rounded-full bg-gradient-to-r from-blue-500 to-teal-600 flex items-center justify-center">
                <span className="text-white text-sm font-bold">üê¶</span>
              </div>
              <h1 className="text-xl font-semibold text-gray-900">AICOMPLYR.io Platform</h1>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">AI Governance Dashboard</h2>
          <p className="text-gray-600 mb-6">
            Welcome to your AI compliance management platform. Click below to view live governance activity.
          </p>
          
          {/* Button to open LiveGovernanceStream */}
          <button
            onClick={() => openStream()}
            className="bg-indigo-600 text-white px-6 py-3 rounded-lg shadow-lg hover:bg-indigo-700 transition-colors flex items-center gap-2"
          >
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
            </svg>
            View Live Governance Activity
          </button>
        </div>
      </div>

      {/* LiveGovernanceStream Component */}
      <LiveGovernanceStream
        isOpen={isStreamOpen}
        onClose={() => setIsStreamOpen(false)}
        currentUser={currentUser}
      />
    </div>
  );
}

export default App;
