import React, { useState, useEffect, useMemo } from 'react';
import { format, parseISO, isWithinInterval, startOfDay, endOfDay } from 'date-fns';
import { 
  Search, 
  Filter, 
  Download, 
  ChevronDown, 
  ChevronUp, 
  Calendar,
  User,
  FileText,
  Shield,
  AlertTriangle,
  CheckCircle,
  Clock,
  Eye,
  EyeOff
} from 'lucide-react';
import jsPDF from 'jspdf';

const DecisionAuditTrail = ({ organizationId }) => {
  // State management
  const [auditEntries, setAuditEntries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [expandedEntries, setExpandedEntries] = useState(new Set());
  const [filters, setFilters] = useState({
    dateRange: { start: null, end: null },
    user: '',
    decisionType: '',
    searchQuery: ''
  });
  const [showFilters, setShowFilters] = useState(false);

  // Fetch audit data
  useEffect(() => {
    fetchAuditData();
  }, [organizationId]);

  const fetchAuditData = async () => {
    try {
      setLoading(true);
      const response = await fetch(`/api/dashboard/audit-trail/${organizationId}`);
      if (!response.ok) {
        throw new Error('Failed to fetch audit data');
      }
      const data = await response.json();
      setAuditEntries(data.auditTrail || []);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Filter and search logic
  const filteredEntries = useMemo(() => {
    return auditEntries.filter(entry => {
      // Date range filter
      if (filters.dateRange.start && filters.dateRange.end) {
        const entryDate = parseISO(entry.timestamp);
        const startDate = startOfDay(filters.dateRange.start);
        const endDate = endOfDay(filters.dateRange.end);
        
        if (!isWithinInterval(entryDate, { start: startDate, end: endDate })) {
          return false;
        }
      }

      // User filter
      if (filters.user && !entry.user?.toLowerCase().includes(filters.user.toLowerCase())) {
        return false;
      }

      // Decision type filter
      if (filters.decisionType && entry.decisionType !== filters.decisionType) {
        return false;
      }

      // Search query
      if (filters.searchQuery) {
        const searchLower = filters.searchQuery.toLowerCase();
        const searchableText = [
          entry.action,
          entry.user,
          entry.decisionType,
          entry.rationale,
          entry.policyReferences?.join(' ')
        ].join(' ').toLowerCase();
        
        if (!searchableText.includes(searchLower)) {
          return false;
        }
      }

      return true;
    });
  }, [auditEntries, filters]);

  // Toggle entry expansion
  const toggleEntry = (entryId) => {
    const newExpanded = new Set(expandedEntries);
    if (newExpanded.has(entryId)) {
      newExpanded.delete(entryId);
    } else {
      newExpanded.add(entryId);
    }
    setExpandedEntries(newExpanded);
  };

  // Export functionality
  const exportToPDF = () => {
    const doc = new jsPDF();
    let yPos = 20;

    doc.setFontSize(20);
    doc.text('Audit Trail Report', 20, yPos);
    yPos += 20;

    doc.setFontSize(12);
    filteredEntries.forEach((entry, index) => {
      if (yPos > 250) {
        doc.addPage();
        yPos = 20;
      }

      doc.setFontSize(14);
      doc.text(`${index + 1}. ${entry.action}`, 20, yPos);
      yPos += 8;

      doc.setFontSize(10);
      doc.text(`Timestamp: ${format(parseISO(entry.timestamp), 'PPpp')}`, 20, yPos);
      yPos += 6;
      doc.text(`User: ${entry.user}`, 20, yPos);
      yPos += 6;
      doc.text(`Decision Type: ${entry.decisionType}`, 20, yPos);
      yPos += 6;
      
      if (entry.rationale) {
        doc.text(`Rationale: ${entry.rationale}`, 20, yPos);
        yPos += 6;
      }

      if (entry.policyReferences?.length) {
        doc.text(`Policy References: ${entry.policyReferences.join(', ')}`, 20, yPos);
        yPos += 6;
      }

      yPos += 10;
    });

    doc.save('audit-trail-report.pdf');
  };

  const exportToCSV = () => {
    const headers = ['Timestamp', 'User', 'Action', 'Decision Type', 'Rationale', 'Policy References'];
    const csvData = [
      headers.join(','),
      ...filteredEntries.map(entry => [
        format(parseISO(entry.timestamp), 'yyyy-MM-dd HH:mm:ss'),
        entry.user || '',
        entry.action,
        entry.decisionType || '',
        `"${(entry.rationale || '').replace(/"/g, '""')}"`,
        `"${(entry.policyReferences?.join('; ') || '').replace(/"/g, '""')}"`
      ].join(','))
    ].join('\n');

    const blob = new Blob([csvData], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'audit-trail-report.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  // Get status badge styling
  const getStatusBadge = (status) => {
    const statusConfig = {
      approved: { color: 'bg-green-100 text-green-800', icon: CheckCircle },
      rejected: { color: 'bg-red-100 text-red-800', icon: AlertTriangle },
      pending: { color: 'bg-yellow-100 text-yellow-800', icon: Clock },
      review: { color: 'bg-blue-100 text-blue-800', icon: Eye }
    };

    const config = statusConfig[status?.toLowerCase()] || statusConfig.pending;
    const IconComponent = config.icon;

    return (
      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${config.color}`}>
        <IconComponent className="w-3 h-3 mr-1" />
        {status || 'pending'}
      </span>
    );
  };

  // Get decision type icon
  const getDecisionTypeIcon = (type) => {
    const iconMap = {
      policy: Shield,
      audit: FileText,
      compliance: CheckCircle,
      risk: AlertTriangle
    };
    const IconComponent = iconMap[type?.toLowerCase()] || FileText;
    return <IconComponent className="w-4 h-4" />;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
        <span className="ml-3 text-gray-600">Loading audit trail...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-md p-4">
        <div className="flex">
          <AlertTriangle className="h-5 w-5 text-red-400" />
          <div className="ml-3">
            <h3 className="text-sm font-medium text-red-800">Error loading audit trail</h3>
            <p className="text-sm text-red-700 mt-1">{error}</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200">
      {/* Header */}
      <div className="px-6 py-4 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-lg font-semibold text-gray-900">Trust & Transparency Audit Trail</h2>
            <p className="text-sm text-gray-600 mt-1">
              Complete timeline of all policy decisions and actions
            </p>
          </div>
          
          <div className="flex items-center space-x-2">
            <button
              onClick={() => setShowFilters(!showFilters)}
              className="inline-flex items-center px-3 py-2 border border-gray-300 shadow-sm text-sm leading-4 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              <Filter className="w-4 h-4 mr-2" />
              Filters
              {showFilters ? <ChevronUp className="w-4 h-4 ml-2" /> : <ChevronDown className="w-4 h-4 ml-2" />}
            </button>
            
            <div className="relative">
              <button
                onClick={exportToPDF}
                className="inline-flex items-center px-3 py-2 border border-gray-300 shadow-sm text-sm leading-4 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                <Download className="w-4 h-4 mr-2" />
                Export PDF
              </button>
            </div>
            
            <button
              onClick={exportToCSV}
              className="inline-flex items-center px-3 py-2 border border-gray-300 shadow-sm text-sm leading-4 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              <Download className="w-4 h-4 mr-2" />
              Export CSV
            </button>
          </div>
        </div>
      </div>

      {/* Filters */}
      {showFilters && (
        <div className="px-6 py-4 border-b border-gray-200 bg-gray-50">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {/* Search */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Search</label>
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                <input
                  type="text"
                  value={filters.searchQuery}
                  onChange={(e) => setFilters(prev => ({ ...prev, searchQuery: e.target.value }))}
                  placeholder="Search entries..."
                  className="pl-10 pr-3 py-2 border border-gray-300 rounded-md w-full focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
            </div>

            {/* Date Range */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Start Date</label>
              <div className="relative">
                <Calendar className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                <input
                  type="date"
                  value={filters.dateRange.start ? format(filters.dateRange.start, 'yyyy-MM-dd') : ''}
                  onChange={(e) => setFilters(prev => ({ 
                    ...prev, 
                    dateRange: { ...prev.dateRange, start: e.target.value ? new Date(e.target.value) : null }
                  }))}
                  className="pl-10 pr-3 py-2 border border-gray-300 rounded-md w-full focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">End Date</label>
              <div className="relative">
                <Calendar className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                <input
                  type="date"
                  value={filters.dateRange.end ? format(filters.dateRange.end, 'yyyy-MM-dd') : ''}
                  onChange={(e) => setFilters(prev => ({ 
                    ...prev, 
                    dateRange: { ...prev.dateRange, end: e.target.value ? new Date(e.target.value) : null }
                  }))}
                  className="pl-10 pr-3 py-2 border border-gray-300 rounded-md w-full focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
            </div>

            {/* Decision Type */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Decision Type</label>
              <select
                value={filters.decisionType}
                onChange={(e) => setFilters(prev => ({ ...prev, decisionType: e.target.value }))}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="">All Types</option>
                <option value="policy">Policy</option>
                <option value="audit">Audit</option>
                <option value="compliance">Compliance</option>
                <option value="risk">Risk Assessment</option>
              </select>
            </div>
          </div>
        </div>
      )}

      {/* Results Summary */}
      <div className="px-6 py-3 bg-gray-50 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <span className="text-sm text-gray-600">
            Showing {filteredEntries.length} of {auditEntries.length} entries
          </span>
          <span className="text-sm text-gray-600">
            Last updated: {format(new Date(), 'PPpp')}
          </span>
        </div>
      </div>

      {/* Timeline */}
      <div className="p-6">
        {filteredEntries.length === 0 ? (
          <div className="text-center py-12">
            <FileText className="mx-auto h-12 w-12 text-gray-400" />
            <h3 className="mt-2 text-sm font-medium text-gray-900">No audit entries found</h3>
            <p className="mt-1 text-sm text-gray-500">
              Try adjusting your filters or search criteria.
            </p>
          </div>
        ) : (
          <div className="space-y-4">
            {filteredEntries.map((entry, index) => (
              <div
                key={entry.id || index}
                className="bg-white border border-gray-200 rounded-lg shadow-sm hover:shadow-md transition-shadow"
              >
                {/* Entry Header */}
                <div
                  className="px-4 py-3 cursor-pointer"
                  onClick={() => toggleEntry(entry.id || index)}
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <div className="flex-shrink-0">
                        <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center">
                          {getDecisionTypeIcon(entry.decisionType)}
                        </div>
                      </div>
                      
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center space-x-2">
                          <h3 className="text-sm font-medium text-gray-900 truncate">
                            {entry.action}
                          </h3>
                          {getStatusBadge(entry.status)}
                        </div>
                        
                        <div className="flex items-center space-x-4 mt-1 text-xs text-gray-500">
                          <div className="flex items-center">
                            <Clock className="w-3 h-3 mr-1" />
                            {format(parseISO(entry.timestamp), 'PPpp')}
                          </div>
                          {entry.user && (
                            <div className="flex items-center">
                              <User className="w-3 h-3 mr-1" />
                              {entry.user}
                            </div>
                          )}
                          {entry.decisionType && (
                            <div className="flex items-center">
                              <FileText className="w-3 h-3 mr-1" />
                              {entry.decisionType}
                            </div>
                          )}
                        </div>
                      </div>
                    </div>
                    
                    <div className="flex-shrink-0">
                      {expandedEntries.has(entry.id || index) ? (
                        <ChevronUp className="w-5 h-5 text-gray-400" />
                      ) : (
                        <ChevronDown className="w-5 h-5 text-gray-400" />
                      )}
                    </div>
                  </div>
                </div>

                {/* Expanded Content */}
                {expandedEntries.has(entry.id || index) && (
                  <div className="px-4 pb-4 border-t border-gray-100">
                    <div className="mt-3 space-y-3">
                      {/* Rationale */}
                      {entry.rationale && (
                        <div>
                          <h4 className="text-xs font-medium text-gray-700 uppercase tracking-wide mb-1">
                            Rationale
                          </h4>
                          <p className="text-sm text-gray-600 bg-gray-50 p-3 rounded-md">
                            {entry.rationale}
                          </p>
                        </div>
                      )}

                      {/* Policy References */}
                      {entry.policyReferences && entry.policyReferences.length > 0 && (
                        <div>
                          <h4 className="text-xs font-medium text-gray-700 uppercase tracking-wide mb-1">
                            Policy References
                          </h4>
                          <div className="flex flex-wrap gap-2">
                            {entry.policyReferences.map((policy, idx) => (
                              <span
                                key={idx}
                                className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800"
                              >
                                <Shield className="w-3 h-3 mr-1" />
                                {policy}
                              </span>
                            ))}
                          </div>
                        </div>
                      )}

                      {/* Additional Details */}
                      {entry.details && (
                        <div>
                          <h4 className="text-xs font-medium text-gray-700 uppercase tracking-wide mb-1">
                            Additional Details
                          </h4>
                          <div className="bg-gray-50 p-3 rounded-md">
                            <pre className="text-xs text-gray-600 whitespace-pre-wrap">
                              {JSON.stringify(entry.details, null, 2)}
                            </pre>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
