// File: ui/components/LiveGovernanceStreamDemo.jsx

import React, { useState, useEffect } from 'react';
import LiveGovernanceStream from './LiveGovernanceStream';

const LiveGovernanceStreamDemo = () => {
  const [events, setEvents] = useState([]);
  const [isExpanded, setIsExpanded] = useState(false);

  // Sample events for demonstration
  const sampleEvents = [
    {
      id: 1,
      type: 'approved',
      title: 'Tool Approved',
      description: 'Runway AI passed compliance check and was approved for use in marketing campaigns',
      timestamp: new Date(Date.now() - 1000 * 60 * 5).toISOString(),
      agent: true
    },
    {
      id: 2,
      type: 'analysis',
      title: 'Policy Analysis Complete',
      description: 'Meta-Loop completed analysis of new FDA guidelines impact on current policies',
      timestamp: new Date(Date.now() - 1000 * 60 * 12).toISOString(),
      agent: true
    },
    {
      id: 3,
      type: 'audit',
      title: 'Audit Generated',
      description: 'Monthly compliance audit report generated for Q4 2024',
      timestamp: new Date(Date.now() - 1000 * 60 * 25).toISOString(),
      agent: true
    },
    {
      id: 4,
      type: 'sync',
      title: 'Policy Sync',
      description: 'Synchronized agency policies with enterprise compliance framework',
      timestamp: new Date(Date.now() - 1000 * 60 * 45).toISOString(),
      agent: true
    },
    {
      id: 5,
      type: 'conflict',
      title: 'Conflict Detected',
      description: 'Potential conflict found between new tool request and existing policy restrictions',
      timestamp: new Date(Date.now() - 1000 * 60 * 60).toISOString(),
      agent: true
    },
    {
      id: 6,
      type: 'rejected',
      title: 'Tool Rejected',
      description: 'ChatGPT integration rejected due to data privacy concerns',
      timestamp: new Date(Date.now() - 1000 * 60 * 90).toISOString(),
      agent: true
    },
    {
      id: 7,
      type: 'approved',
      title: 'Agency Seat Created',
      description: 'New agency seat "PharmaCorp Marketing" created successfully',
      timestamp: new Date(Date.now() - 1000 * 60 * 120).toISOString(),
      agent: true
    },
    {
      id: 8,
      type: 'analysis',
      title: 'Risk Assessment',
      description: 'Completed risk assessment for new AI-powered content generation tools',
      timestamp: new Date(Date.now() - 1000 * 60 * 180).toISOString(),
      agent: true
    }
  ];

  // Simulate real-time events
  useEffect(() => {
    // Initialize with sample events
    setEvents(sampleEvents);

    // Add new events every 5-15 seconds
    const interval = setInterval(() => {
      const newEventTypes = [
        {
          type: 'approved',
          title: 'Policy Update Approved',
          description: 'Updated social media policy approved by compliance team',
          agent: true
        },
        {
          type: 'analysis',
          title: 'Compliance Check',
          description: 'Meta-Loop analyzed recent policy changes for regulatory alignment',
          agent: true
        },
        {
          type: 'sync',
          title: 'Data Sync Complete',
          description: 'Synchronized user permissions across all agency seats',
          agent: true
        },
        {
          type: 'audit',
          title: 'Security Audit',
          description: 'Completed quarterly security audit for data access controls',
          agent: true
        },
        {
          type: 'conflict',
          title: 'Permission Conflict',
          description: 'Detected conflicting permissions between user roles',
          agent: true
        }
      ];

      const randomEvent = newEventTypes[Math.floor(Math.random() * newEventTypes.length)];
      const newEvent = {
        id: Date.now(),
        ...randomEvent,
        timestamp: new Date().toISOString()
      };

      setEvents(prev => [newEvent, ...prev.slice(0, 9)]); // Keep max 10 events
    }, 5000 + Math.random() * 10000);

    return () => clearInterval(interval);
  }, []);

  const handleToggleShowMore = (expanded) => {
    setIsExpanded(expanded);
  };

  const addTestEvent = (type) => {
    const eventTemplates = {
      approved: {
        title: 'Test Approval',
        description: 'This is a test approval event for demonstration purposes'
      },
      conflict: {
        title: 'Test Conflict',
        description: 'This is a test conflict detection event for demonstration'
      },
      audit: {
        title: 'Test Audit',
        description: 'This is a test audit completion event for demonstration'
      },
      analysis: {
        title: 'Test Analysis',
        description: 'This is a test Meta-Loop analysis event for demonstration'
      },
      sync: {
        title: 'Test Sync',
        description: 'This is a test policy synchronization event for demonstration'
      },
      rejected: {
        title: 'Test Rejection',
        description: 'This is a test rejection event for demonstration purposes'
      }
    };

    const template = eventTemplates[type];
    const newEvent = {
      id: Date.now(),
      type,
      title: template.title,
      description: template.description,
      timestamp: new Date().toISOString(),
      agent: true
    };

    setEvents(prev => [newEvent, ...prev.slice(0, 9)]);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-3">
              <div className="h-8 w-8 rounded-full bg-gradient-to-r from-blue-500 to-teal-600 flex items-center justify-center">
                <span className="text-white text-sm font-bold">üê¶</span>
              </div>
              <h1 className="text-xl font-semibold text-gray-900">Live Governance Stream Demo</h1>
            </div>
            <div className="flex items-center space-x-2">
              <span className="text-sm text-gray-500">Real-time events</span>
              <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Live Stream */}
          <div className="lg:col-span-2">
            <LiveGovernanceStream 
              events={events}
              height="600px"
              showMore={true}
              onToggleShowMore={handleToggleShowMore}
            />
          </div>

          {/* Controls and Info */}
          <div className="space-y-6">
            {/* Test Controls */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">Test Controls</h2>
              <div className="space-y-3">
                <p className="text-sm text-gray-600 mb-3">Add test events to see the stream in action:</p>
                <div className="grid grid-cols-2 gap-2">
                  {['approved', 'conflict', 'audit', 'analysis', 'sync', 'rejected'].map((type) => (
                    <button
                      key={type}
                      onClick={() => addTestEvent(type)}
                      className="px-3 py-2 text-xs font-medium rounded border transition-colors duration-200 hover:shadow-sm"
                      style={{
                        backgroundColor: type === 'approved' ? '#f0fdf4' : 
                                       type === 'conflict' ? '#fefce8' :
                                       type === 'audit' ? '#eff6ff' :
                                       type === 'analysis' ? '#faf5ff' :
                                       type === 'sync' ? '#eef2ff' :
                                       '#fef2f2',
                        borderColor: type === 'approved' ? '#bbf7d0' :
                                   type === 'conflict' ? '#fde68a' :
                                   type === 'audit' ? '#bfdbfe' :
                                   type === 'analysis' ? '#e9d5ff' :
                                   type === 'sync' ? '#c7d2fe' :
                                   '#fecaca',
                        color: type === 'approved' ? '#166534' :
                               type === 'conflict' ? '#a16207' :
                               type === 'audit' ? '#1e40af' :
                               type === 'analysis' ? '#7c3aed' :
                               type === 'sync' ? '#4338ca' :
                               '#dc2626'
                      }}
                    >
                      {type.charAt(0).toUpperCase() + type.slice(1)}
                    </button>
                  ))}
                </div>
              </div>
            </div>

            {/* Event Types */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">Event Types</h2>
              <div className="space-y-3">
                {[
                  { type: 'approved', icon: '‚úÖ', color: 'green', desc: 'Successful approvals and positive outcomes' },
                  { type: 'conflict', icon: '‚ö†Ô∏è', color: 'yellow', desc: 'Detected conflicts or potential issues' },
                  { type: 'audit', icon: 'üìÑ', color: 'blue', desc: 'Audit reports and compliance checks' },
                  { type: 'analysis', icon: 'üß†', color: 'purple', desc: 'Meta-Loop AI analysis and insights' },
                  { type: 'sync', icon: 'üîÅ', color: 'indigo', desc: 'Policy synchronization and updates' },
                  { type: 'rejected', icon: '‚ùå', color: 'red', desc: 'Rejections and failed validations' }
                ].map((eventType) => (
                  <div key={eventType.type} className="flex items-center space-x-3">
                    <span className="text-lg">{eventType.icon}</span>
                    <div className="flex-1">
                      <h4 className="text-sm font-medium text-gray-900 capitalize">{eventType.type}</h4>
                      <p className="text-xs text-gray-500">{eventType.desc}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Features */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">Features</h2>
              <div className="space-y-2">
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span className="text-sm text-gray-700">Real-time event streaming</span>
                </div>
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span className="text-sm text-gray-700">Auto-scroll to latest events</span>
                </div>
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span className="text-sm text-gray-700">New event highlighting</span>
                </div>
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span className="text-sm text-gray-700">Responsive design</span>
                </div>
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span className="text-sm text-gray-700">Smooth animations</span>
                </div>
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span className="text-sm text-gray-700">Meta-Loop agent badges</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LiveGovernanceStreamDemo;
